import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import DeleteCliente from './DeleteCliente';
import style from './output.css';

function ClienteList() {
    const [clientes, setClientes] = useState([]);

    useEffect(() => {
        fetchClientes();
    }, []);

    const fetchClientes = () => {
        axios.get('http://localhost:5000/api/clients')
            .then(response => setClientes(response.data))
            .catch(error => console.error('Erro ao buscar clientes', error));
    };

    return (
        <div className="p-4">
            <h2 className="text-2xl font-semibold ">Lista de Clientes</h2>
            <ul>
                {clientes.map(cliente => (
                    <li key={cliente._id} className="flex items-center justify-between py-2">
                        <span className="text-sm text-gray-700 mr-3">{cliente.name} - {cliente.email}</span>
                        <div className="flex items-center">
                            <Link
                                to={`/edit/${cliente._id}`}
                                className="text-white bg-blue-500 hover:bg-blue-700 transition-colors px-3 py-1 rounded text-sm mr-2"
                            >
                                Editar
                            </Link>
                            <DeleteCliente
                                id={cliente._id}
                                className="text-white bg-red-500 hover:bg-red-700 transition-colors px-3 py-1 rounded text-sm"
                                onSuccess={() => setClientes(clientes.filter(c => c._id !== cliente._id))}
                            />
                        </div>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default ClienteList;
