import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import DeleteCliente from './DeleteCliente';

// Definição dos estilos para os botões
const styles = {
    linkButton: {
        marginRight: '10px',
        padding: '8px 16px',
        backgroundColor: '#4CAF50', // Cor verde para editar
        color: 'white',
        textDecoration: 'none', // Remover o sublinhado dos links
        border: 'none',
        borderRadius: '4px',
        cursor: 'pointer'
    },
    deleteButton: {
        marginLeft: '10px',
        padding: '8px 16px',
        backgroundColor: '#f44336', // Cor vermelha para excluir
        color: 'white',
        border: 'none',
        borderRadius: '4px',
        cursor: 'pointer'
    }
};

function ClienteList() {
    const [clientes, setClientes] = useState([]);

    useEffect(() => {
        fetchClientes();
    }, []);

    const fetchClientes = () => {
        axios.get('http://localhost:5000/api/clients')
            .then(response => {
                setClientes(response.data);
            })
            .catch(error => {
                console.error('Erro ao buscar clientes', error);
            });
    };

    return (
        <div>
            <h2>Lista de Clientes</h2>
            <ul>
                {clientes.map(cliente => (
                    <li key={cliente._id}>
                        {cliente.name} - {cliente.email}
                        <Link to={`/edit/${cliente._id}`} style={styles.linkButton}>Editar</Link>
                        <DeleteCliente id={cliente._id} onSuccess={fetchClientes} style={styles.deleteButton} />
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default ClienteList;
