import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import DeleteCliente from './DeleteCliente';

// Estilos locais
const styles = {
    linkButton: {
        padding: '6px 12px',
        backgroundColor: '#4CAF50',
        color: 'white',
        textDecoration: 'none',
        border: 'none',
        borderRadius: '4px',
        cursor: 'pointer',
        fontSize: '14px'
    },
    deleteButton: {
        marginLeft: '10px',
        padding: '6px 12px',
        backgroundColor: '#f44336',
        color: 'white',
        border: 'none',
        borderRadius: '4px',
        cursor: 'pointer',
        fontSize: '14px'
    },
    clientText: {
        marginRight: '20px',
        display: 'inline-block'
    }
};

function ClienteList() {
    const [clientes, setClientes] = useState([]);

    useEffect(() => {
        fetchClientes();
    }, []);

    const fetchClientes = () => {
        axios.get('http://localhost:5000/api/clients')
            .then(response => setClientes(response.data))
            .catch(error => console.error('Erro ao buscar clientes', error));
    };

    const handleDeleteSuccess = (id) => {
        const updatedClientes = clientes.filter(cliente => cliente._id !== id);
        setClientes(updatedClientes);
    };

    return (
        <div>
            <h2>Lista de Clientes</h2>
            <ul>
                {clientes.map(cliente => (
                    <li key={cliente._id}>
                        <div style={styles.clientText}>{cliente.name} - {cliente.email}</div>
                        <Link to={`/edit/${cliente._id}`} style={styles.linkButton}>Editar</Link>
                        <DeleteCliente id={cliente._id} onSuccess={() => handleDeleteSuccess(cliente._id)} />
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default ClienteList;
