import React, { useState, useEffect } from 'react';
import axios from 'axios';

function EditCliente({ match }) {
    const [cliente, setCliente] = useState({
        name: '',
        email: '',
        phone: '',
        address: ''
    });

    useEffect(() => {
        axios.get(`http://localhost:5000/api/clients/${match.params.id}`)
            .then(response => {
                setCliente(response.data);
            })
            .catch(error => {
                console.error('Erro ao buscar dados do cliente', error);
            });
    }, [match.params.id]);

    const handleChange = (e) => {
        setCliente({ ...cliente, [e.target.name]: e.target.value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        axios.put(`http://localhost:5000/api/clients/${match.params.id}`, cliente)
            .then(() => {
                alert('Cliente atualizado com sucesso!');
                // Opcional: redirecionar o usuário ou realizar outra ação
            })
            .catch(error => {
                console.error('Erro ao atualizar cliente', error);
            });
    };

    return (
        <form onSubmit={handleSubmit}>
            <h2>Editar Cliente</h2>
            <label>
                Nome:
                <input type="text" name="name" value={cliente.name} onChange={handleChange} />
            </label>
            <label>
                Email:
                <input type="email" name="email" value={cliente.email} onChange={handleChange} />
            </label>
            <label>
                Telefone:
                <input type="text" name="phone" value={cliente.phone} onChange={handleChange} />
            </label>
            <label>
                Endereço:
                <input type="text" name="address" value={cliente.address} onChange={handleChange} />
            </label>
            <button type="submit">Atualizar</button>
        </form>
    );
}

export default EditCliente;
