import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import DeleteCliente from './DeleteCliente';

function ClienteList() {
    const [clientes, setClientes] = useState([]);

    useEffect(() => {
        fetchClientes();
    }, []);

    const fetchClientes = () => {
        axios.get('http://localhost:5000/api/clients')
            .then(response => setClientes(response.data))
            .catch(error => console.error('Erro ao buscar clientes', error));
    };

    const handleDeleteSuccess = (id) => {
        const updatedClientes = clientes.filter(cliente => cliente._id !== id);
        setClientes(updatedClientes);
    };

    return (
        <div className="p-4">
            <h2 className="text-2xl font-semibold mb-2">Lista de Clientes</h2>
            <ul>
                {clientes.map(cliente => (
                    <li key={cliente._id} className="flex justify-between items-center py-2 border-b border-gray-300">
                        <div className="mr-2">{cliente.name} - {cliente.email}</div>
                        <div className="flex">
                            <Link
                                to={`/edit/${cliente._id}`}
                                className="bg-green-500 text-white px-3 py-1 rounded-md mr-2 hover:bg-green-700 transition-colors duration-200"
                            >
                                Editar
                            </Link>
                            <DeleteCliente
                                id={cliente._id}
                                className="bg-red-500 text-white px-3 py-1 rounded-md hover:bg-red-700 transition-colors duration-200"
                                onSuccess={() => handleDeleteSuccess(cliente._id)}
                            />
                        </div>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default ClienteList;
